const sliderMov = document.querySelector("#slider");

let sliderSection = document.querySelectorAll (".slider--section");

let sliderSectionLast = sliderSection[sliderSection.length-1];

const btnLeft = document.querySelector("#btn--left");
const btnRight = document.querySelector("#btn--right");

slider.insertAdjacentElement('afterbegin', sliderSectionLast) ;


function moveDerecha(){
    let sliderSectionFirst = document.querySelectorAll(".slider--section")[0];
    sliderMov.style.marginLeft = "-200%";
    sliderMov.style.transition = "all 1s";
    setTimeout(function(){
        sliderMov.style.transition = "none";
        sliderMov.insertAdjacentElement('beforeend', sliderSectionFirst);
        sliderMov.style.marginLeft = "-100%";
    }, 1000);
    
}

btnRight.addEventListener('click', function() {
    moveDerecha();
}); 

function moveIzquierda(){
    let sliderSection = document.querySelectorAll (".slider--section");
    let sliderSectionLast = sliderSection[sliderSection.length-1];
    sliderMov.style.marginLeft = "0";
    sliderMov.style.transition = "all 1s";
    setTimeout(function(){
        sliderMov.style.transition = "none";
        sliderMov.insertAdjacentElement('afterbegin', sliderSectionLast);
        sliderMov.style.marginLeft = "-100%";
    }, 1000);
    
}

btnLeft.addEventListener('click', function() {
    moveIzquierda();
}); 

setInterval(function(){
    moveDerecha();
    
}, 3000);

export default sliderMov
