import { NavLink } from "react-router-dom"
const Slider = () => (
    <div className="container-slider">
        <div C="slider" id="slider">
            <div class="slider--section"><img src="/img-1.jpg" alt="" class="slider--img"/></div>
            <div class="slider--section"><img src="/img-2.jpg" alt="" class="slider--img"/></div>
            <div class="slider--section"><img src="/img-3.jpg" alt="" class="slider--img"/></div>
            <div class="slider--section"><img src="/img-4.jpg" alt="" class="slider--img"/></div>
            <div class="slider--section"><img src="/img-5.jpg" alt="" class="slider--img"/></div>
            <div class="slider--section"><img src="/img-6.jpg" alt="" class="slider--img"/></div>
            <div class="slider--section"><img src="/img-7.jpg" alt="" class="slider--img"/></div>
        </div>
        <div class="slider--btn slider--btn--right" id="btn--right">&#62;</div>
        <div class="slider--btn slider--btn--left" id="btn--left">&#60;</div>

    </div>          
)

export default Slider





const slider = document.querySelector("#slider");

let sliderSection = document.querySelectorAll (".slider--section");

let sliderSectionLast = sliderSection[sliderSection.length-1];

const btnLeft = document.querySelector("#btn--left");
const btnRight = document.querySelector("#btn--right");

slider.insertAdjacentElement('afterbegin', sliderSectionLast) ;


function moveDerecha(){
    let sliderSectionFirst = document.querySelectorAll(".slider--section")[0];
    slider.style.marginLeft = "-200%";
    slider.style.transition = "all 1s";
    setTimeout(function(){
        slider.style.transition = "none";
        slider.insertAdjacentElement('beforeend', sliderSectionFirst);
        slider.style.marginLeft = "-100%";
    }, 1000);
    
}

btnRight.addEventListener('click', function() {
    moveDerecha();
}); 

function moveIzquierda(){
    let sliderSection = document.querySelectorAll (".slider--section");
    let sliderSectionLast = sliderSection[sliderSection.length-1];
    slider.style.marginLeft = "0";
    slider.style.transition = "all 1s";
    setTimeout(function(){
        slider.style.transition = "none";
        slider.insertAdjacentElement('afterbegin', sliderSectionLast);
        slider.style.marginLeft = "-100%";
    }, 1000);
    
}

btnLeft.addEventListener('click', function() {
    moveIzquierda();
}); 

setInterval(function(){
    moveDerecha();
    
}, 3000);
